import { FetchClientResponse } from "./response";

interface IRequestProps {
	body?: Record<string, any>;
	query?: Record<string | number, string | number>;
	config?: IConfigProps;
	otherType?: "formData" | "excel";
}

interface IConfigProps {
	cache?: RequestCache;
	integrity?: string;
	keepalive?: boolean;
	mode?: RequestMode;
	priority?: RequestPriority;
	redirect?: RequestRedirect;
	referrer?: string;
	referrerPolicy?: ReferrerPolicy;
	signal?: AbortSignal | null;
	window?: null;
}

type ClientResponse<T> = Promise<FetchClientResponse<T>>;

export { IRequestProps, IConfigProps, ClientResponse };
